import axios from "axios";
export default {
  props: {
    navTitle: String,
    navSubTitle: String
  },

  data() {
    return {
      allCountries: [],
      countries: []
    };
  },

  methods: {
    searchResults(search = "all") {
      axios.get( // `https://restcountries.com/v3.1/${
      //   this.$refs.input.value == "" ? "all" : "name/" + search
      // }`
      `https://restcountries.com/v3.1/name/${search}`).then(response => {
        this.countries = response.data;

        for (let i = 0; i < 10 && i < this.countries.length; i++) {
          this.allCountries.push(this.countries[i]);
        }
      });
      return this.allCountries;
    },

    search() {
      if (this.$refs.input == "") {
        this.searchResults();
      } else {
        this.searchResults(this.$refs.input.value);
      }
    }

  }
};
